mapper.select:
select *
from complaint_distribution_management_info
where right_type = #{rightType}
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
  and complaint_platform = #{complaintPlatform}
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
  and node = #{node}
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
  and distribution_name like "%"#{distributionName}"%"
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
  and operator_name like "%"#{operatorName}"%"
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
order by ${time} ${sort}
         limit #{num1},#{num2}

select *
from complaint_distribution_management_info
where right_type = #{rightType}
  and ${timeType} > #{startTime}
  and ${timeType} < #{endTime}
order by ${time} ${sort}
         limit #{num1},#{num2}

select count(*)
from complaint_distribution_management_info
where right_type = #{rightType}

select count(*)
from complaint_distribution_management_info
where right_type = #{rightType}
  and complaint_platform = #{complaintPlatform}

select count(*)
from complaint_distribution_management_info
where right_type = #{rightType}
  and node = #{node}

select count(*)
from complaint_distribution_management_info
where right_type = #{rightType}
  and ${timeType} > #{startTime}
  and ${timeType} < #{endTime}
