mapper.select:

select count(*)
from (select id id
      from
          copyright_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
      union all
      select id id
      FROM reputation_portrait_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
      union all
      select id id
      from other_right_info
      where user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}) s


select count(*)
from (select id id
      from
          copyright_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_state = #{auditState}
      union all
      select id id
      FROM reputation_portrait_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_status = #{auditState}
      union all
      select id id
      from other_right_info
      where user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_status = #{auditState}) s


select *
from (select id                 id,
             copyright_type     right_type,
             works_name         right_name,
             is_right_entrusted is_right_entrusted,
             audit_state        audit_state,
             gmt_create         create_time,
             gmt_modified       updateTime
      from
          copyright_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_state = #{auditState}
      union all
      select id                 id,
             copyright_type     right_type,
             copyright_name     right_name,
             is_right_entrusted is_right_entrusted,
             audit_status       state,
             gmt_create         create_time,
             gmt_modified       updateTime
      FROM reputation_portrait_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_status = #{auditState}
      union all
      select id                 id,
             copyright_type     right_type,
             copyright_name     works_name,
             is_right_entrusted is_right_entrusted,
             audit_status       audit_state,
             gmt_create         create_time,
             gmt_modified       updateTime
      from other_right_info
      where user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
        and audit_status = #{auditState}) s
order by s.create_time desc limit #{num1},#{num2}


select count(*)
from (select id             id,
             copyright_type right_type

      from
          copyright_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
      union all
      select id             id,
             copyright_type right_type
      FROM reputation_portrait_info
      WHERE user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}
      union all
      select id             id,
             copyright_type right_type
      from other_right_info
      where user_id = #{userId}
        and gmt_create < #{t2}
        and gmt_create > #{t1}) s
where s.right_type = #{rightType}


